import {useEffect,useState} from "react";
import {useParams,Link} from "react-router-dom";
import api from "../api";

export default function Category(){

const {id}=useParams();

const [questions,setQuestions]=useState([]);
const [loading,setLoading]=useState(true);

useEffect(()=>{

// â­ THIS IS THE CRITICAL FIX
api.get("/questions/category/"+id)

.then(r=>{
setQuestions(Array.isArray(r.data)?r.data:[]);
setLoading(false);
})

.catch(err=>{
console.log("CATEGORY LOAD ERROR:",err);
setQuestions([]);
setLoading(false);
});

},[id]);



if(loading){
return(
<div className="container mt-4">
<h3>Loading questions...</h3>
</div>
);
}



return(

<div className="container mt-4">

<h2 className="mb-4">â˜• Questions</h2>

<Link className="btn btn-dark mb-3" to="/ask">
Ask Question
</Link>


{questions.length===0 &&

<div className="alert alert-secondary">
No questions yet â€” be the first ðŸ™‚
</div>

}



{questions.map(q=>

<div key={q.id} className="card shadow-sm mb-3 p-3">

<h5>{q.title}</h5>

<p>{q.content}</p>

<small className="text-muted">
Posted by {q.username || "user"}
</small>

</div>

)}

</div>

);

}
