import {useNavigate,Link} from "react-router-dom";
import {useState} from "react";
import api from "../api";

export default function Login(){

const nav=useNavigate();

const [email,setEmail]=useState("");
const [password,setPassword]=useState("");
const [err,setErr]=useState("");

async function login(e){
e.preventDefault();

try{

const r=await api.post("/auth/login",{email,password});

localStorage.setItem("user",r.data.id);

nav("/dashboard");

}catch{
setErr("Invalid email or password");
}

}

return(

<div className="container mt-5" style={{maxWidth:450}}>

{/* ===== BEANTALK TITLE ===== */}

<h1 className="text-center fw-bold mb-1">
â˜• BeanTalk
</h1>

<div className="text-center text-muted mb-4">
Coffee Community Discussion Platform
</div>


{/* ===== LOGIN CARD ===== */}

<div className="card shadow-lg p-4">

<h3 className="fw-bold mb-1">
Welcome 
</h3>

<div className="text-muted mb-3">
Login to continue your coffee discussions
</div>

<form onSubmit={login}>

<input
className="form-control mb-2"
placeholder="Email"
onChange={e=>setEmail(e.target.value)}
required
/>

<input
type="password"
className="form-control mb-3"
placeholder="Password"
onChange={e=>setPassword(e.target.value)}
required
/>

<button className="btn btn-dark w-100 mb-2">
Login
</button>

{err && <div className="text-danger mb-2">{err}</div>}

<div className="text-center">
<Link to="/register">
Create an account
</Link>
</div>

</form>

</div>

</div>

);
}
