import {useEffect,useState} from "react";
import api from "../api";
import {Link,useNavigate} from "react-router-dom";

export default function Dashboard(){

const [cats,setCats]=useState([]);
const nav=useNavigate();

useEffect(()=>{

api.get("/categories")
.then(r=>{
console.log("CATEGORIES:",r.data);

// ensures cats is ALWAYS an array
setCats(Array.isArray(r.data) ? r.data : []);

})
.catch(()=>setCats([]));

},[]);

function logout(){
localStorage.clear();
nav("/");
}

return(

<div className="container mt-4">

<div className="d-flex justify-content-between">

<h2 className="fw-bold">☕ BeanTalk Dashboard</h2>

<button className="btn btn-danger" onClick={logout}>
Logout
</button>

</div>

<hr/>

<Link className="btn btn-success mb-4" to="/ask">
+ Ask Coffee Question
</Link>

<div className="row">

{cats.map(c=>(

<div className="col-md-4" key={c.id}>

<div className="card shadow-sm border-0 p-4 mb-4">

<h5>{c.name}</h5>

<Link className="btn btn-dark btn-sm"
to={"/category/"+c.id}>
Open
</Link>

</div>

</div>

))}

</div>

{cats.length===0 && (

<div className="text-center mt-5">

<h5>No discussions yet</h5>

<p className="text-muted">
Start the first coffee conversation ☕
</p>

</div>

)}

</div>

);

}
